ndpointsApiExplorer();","builder.Services.AddSwaggerGen();","","var app = builder.Build();","","// Configure the HTTP request pipeline.","if (app.Environment.IsDevelopment())","{","    app.UseSwagger();","    app.UseSwaggerUI();","}","","//app.UseHttpsRedirection();","var port = 8080;","var frontc = 8081; ","// Mostrar la URL completa en consola","try","{","    using var client = new HttpClient();","    var publicIp = client.GetStringAsync(\"http://checkip.amazonaws.com\").Result.Trim();","    Console.WriteLine($\"API escuchando en: http://0.0.0.0:{port}\");","    Console.WriteLine($\"Para acceder desde tu IP pública de AWS usa: http://{publicIp}:{port}\");","    Console.WriteLine($\"Para acceder al frontend de AWS usa: http://{publicIp}:{frontc}\");","}","catch","{","    Console.WriteLine($\"API escuchando en http://0.0.0.0:{port}\");","}","","var summaries = new[]","{","    \"Freezing\", \"Bracing\", \"Chilly\", \"Cool\", \"Mild\", \"Warm\", \"Balmy\", \"Hot\", \"Sweltering\", \"Scorching\"","};","","app.MapGet(\"/\", () => Results.Redirect(\"/inicio\"));","","app.MapGet(\"/inicio\", () => \"¡Bienvenido a Inicio!\");","","","app.MapGet(\"/weatherforecast\", () =>","{","    var forecast =  Enumerable.Range(1, 5).Select(index =>","        new WeatherForecast","        (","            DateOnly.FromDateTime(DateTime.Now.AddDays(index)),","            Random.Shared.Next(-20, 55),","            summaries[Random.Shared.Next(summaries.Length)]","        ))","        .ToArray();","    return forecast;","})",".WithName(\"GetWeatherForecast\")",".WithOpenApi();","","app.Run($\"http://0.0.0.0:{port}\");","","record WeatherForecast(DateOnly Date, int TemperatureC, string? Summary)","{","    public int TemperatureF => 32 + (int)(TemperatureC / 0.5556);","}",""],"id":564},{"start":{"row":0,"column":0},"end":{"row":152,"column":0},"action":"insert","lines":["using Microsoft.EntityFrameworkCore;","","var builder = WebApplication.CreateBuilder(args);","","// Configurar conexión a MySQL","builder.Services.AddDbContext<ApiDbContext>(options =>","    options.UseMySql(","        builder.Configuration.GetConnectionString(\"DefaultConnection\"),","        new MySqlServerVersion(new Version(8, 0, 36))","    ));","","builder.Services.AddOpenApi();","","var app = builder.Build();","","if (app.Environment.IsDevelopment())","{","    app.MapOpenApi();","}","","// Endpoints -------------------------------","","// Endpoint raíz","app.MapGet(\"/\", () => Results.Redirect(\"/inicio\"));","","app.MapGet(\"/inicio\", () => \"¡Bienvenido a Inicio!\");","","// Endpoint GET: obtener todos los usuarios","app.MapGet(\"/usuarios\", async (ApiDbContext db) =>","    await db.users","        .Select(u => new UsersDto","        {","            id = u.id,","            email = u.email,","            first_name = u.first_name,","            last_name = u.last_name,","            phone = u.phone,","            curp = u.curp,","            birth_date = u.birth_date,","            address = u.address,","            user_type = u.user_type,","            is_active = u.is_active,","            created_at = u.created_at","        })","        .ToListAsync())","    .WithName(\"ObtenerUsuarios\");","","// Endpoint GET: obtener usuario por ID","app.MapGet(\"/usuario/{id}\", async (ApiDbContext db, int id) =>","{","    var user = await db.users","        .Where(u => u.id == id)","        .Select(u => new UsersDto","        {","            id = u.id,","            email = u.email,","            first_name = u.first_name,","            last_name = u.last_name,","            phone = u.phone,","            curp = u.curp,","            birth_date = u.birth_date,","            address = u.address,","            user_type = u.user_type,","            is_active = u.is_active,","            created_at = u.created_at","        })","        .FirstOrDefaultAsync();","","    return user is not null","        ? Results.Ok(user)","        : Results.NotFound(new { mensaje = \"Usuario no encontrado\" });","})",".WithName(\"ObtenerUsuarioPorId\");","","","// Función auxiliar para calcular edad","int CalcularEdad(DateTime birth_date)","{","    var hoy = DateTime.Today;","    int edad = hoy.Year - birth_date.Year;","","    if (birth_date.Date > hoy.AddYears(-edad))","        edad--;","","    return edad;","}","","// Endpoint POST: agregar usuario","app.MapPost(\"/usuario\", async (ApiDbContext db, Users users) =>","{","    // Validar edad","    int edad = CalcularEdad(users.birth_date);","    if (edad < 12 || edad > 30)","    {","        return Results.BadRequest(new { mensaje = $\"La edad de {users.first_name} {users.last_name} no está permitida (debe estar entre 12 y 30 años)\" });","    }","","    db.users.Add(users);","    await db.SaveChangesAsync();","    return Results.Ok(new { mensaje = $\"Usuario {users.first_name} {users.last_name} agregado correctamente\" });","})",".WithName(\"AgregarUsuario\");","","","// Endpoint para agregar múltiples usuarios ","app.MapPost(\"/usuarios\", async (ApiDbContext db, List<Users> users) =>","{","    if (users == null || users.Count == 0)","        return Results.BadRequest(new { mensaje = \"No se enviaron usuarios\" });","","    foreach (var user in users)","    {","        // Asegurarse que los campos obligatorios estén completos","        if (string.IsNullOrWhiteSpace(user.first_name) || string.IsNullOrWhiteSpace(user.curp))","            return Results.BadRequest(new { mensaje = \"Nombre y CURP son obligatorios\" });","","        // Validar fecha de nacimiento","        if (user.birth_date > DateTime.Today)","            return Results.BadRequest(new { mensaje = $\"Fecha de nacimiento inválida para {user.first_name}\" });","","        // Validar edad","        int edad = CalcularEdad(user.birth_date);","        if (edad < 12 || edad > 30)","            return Results.BadRequest(new { mensaje = $\"La edad de {user.first_name} no está permitida (debe estar entre 12 y 30 años)\" });","","        db.users.Add(user);","    }","","    await db.SaveChangesAsync();","    return Results.Ok(new { mensaje = $\"{users.Count} usuarios agregados correctamente\" });","})",".WithName(\"AgregarUsuariosMultiples\");","","","","app.MapDelete(\"/usuario/{id:int}\", async (ApiDbContext db, int id) =>","{","    var usuario = await db.users.FindAsync(id);","    if (usuario == null)","    {","        return Results.NotFound(new { mensaje = \"Usuario no encontrado\" });","    }","","    db.users.Remove(usuario);","    await db.SaveChangesAsync();","    return Results.Ok(new { mensaje = $\"Usuario {usuario.first_name} {usuario.last_name} eliminado correctamente\" });","})",".WithName(\"EliminarUsuario\");","","","","app.Run();",""]}]]},"ace":{"folds":[],"scrolltop":271.2,"scrollleft":0,"selection":{"start":{"row":43,"column":10},"end":{"row":43,"column":10},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":17,"state":"start","mode":"ace/mode/csharp"}},"timestamp":1758752225411,"hash":"a2474d816172996b0918c07bc4b2862fb2af2f7d"}